CREATE TABLE `schema_migrations` (`version` varchar(255) NOT NULL);
CREATE TABLE `parties` (`id` INTEGER PRIMARY KEY auto_increment NOT NULL, `nbre_joueur` integer, `tour` integer DEFAULT -1, `pioche` varchar(255), `created_at` datetime, `updated_at` datetime, `nom` varchar(255), `createur` integer, `phase` integer DEFAULT '0', `message` varchar(255), `nbre_guess` integer);
CREATE TABLE `joueurs` (`id` INTEGER PRIMARY KEY auto_increment NOT NULL, `pseudo` varchar(255), `hashed_password` varchar(255), `salt` varchar(255), `created_at` datetime, `updated_at` datetime);
CREATE TABLE `sessions` (`id` INTEGER PRIMARY KEY auto_increment NOT NULL, `session_id` varchar(255) NOT NULL, `data` text, `created_at` datetime, `updated_at` datetime);
CREATE TABLE `joueurs_parties` (`id` INTEGER PRIMARY KEY auto_increment NOT NULL, `code` varchar(255), `en_jeu` boolean DEFAULT true, `joueur_id` integer, `partie_id` integer, `created_at` datetime, `updated_at` datetime, `dernier_jeton` varchar(255), `up_to_date` boolean, `last_chat_message` integer DEFAULT 0);
CREATE TABLE `chats` (`id` INTEGER PRIMARY KEY auto_increment NOT NULL, `message` varchar(255), `partie_id` integer, `created_at` datetime, `updated_at` datetime);
CREATE UNIQUE INDEX `unique_schema_migrations` ON `schema_migrations` (`version`);
CREATE INDEX `index_sessions_on_session_id` ON `sessions` (`session_id`);
CREATE INDEX `index_sessions_on_updated_at` ON `sessions` (`updated_at`);
